import { useState, useEffect } from "react";
import { db } from "../firebase"
import { getDocs, collection } from "firebase/firestore";

export default function PopUpLogs( { Turn, setTurn } ) {
    const [data, setData] = useState([]);

    const stylePopUp = "flex-col h-screen w-full bg-black/50 absolute top-0 left-0 z-10 items-center justify-center"

    useEffect(() => {
        async function fetchData() {
            setData([]);
            try {
                const getData = await getDocs(collection(db, "Logs"));
                getData.forEach((doc,i) => {
                    console.log(i);
                    doc.data().history.forEach((history,j) => {
                        // setData(oldData => [...oldData, {
                        //     id:doc.data().ID,
                        //     Date:history.Date,
                        //     TimeOpen:history.TimeOpen,
                        //     TimeClose:history.TimeClose,
                        //     Cooldown:history.Cooldown
                        // }])
                        setData(oldData => [...oldData,<p key={i*j}>{doc.data().ID} - {history.Date}</p>]);
                    });
                });
                // console.log(data);
            }
            catch (err) {
                console.log(err.message);
            }
        };
        fetchData();
    }, [Turn]);

    function handleClick(e) {
        e.preventDefault();
        setTurn(!Turn);
    }
    return (
        <div className={Turn ? "flex " + stylePopUp : "hidden " + stylePopUp}>
            <h1 onClick={handleClick}>close</h1>
            {data}
        </div>
    )
}
