import { useState, useEffect } from "react";
import { db } from "../firebase"
import { getDocs, collection } from "firebase/firestore";
import NavBar from "./navbar"
import PipeStatusBox from "../components/pipeStatusBox"
import PopUpSetTime from "../components/popUpSetTime"


export default function MainPage() {
    const [idFaucet,setIdFaucet] = useState("1");
    const [today,setToday] = useState(new Date());
    const [date, setDate] = useState(today.getFullYear() + '-' + ('0' + (today.getMonth() + 1)).slice(-2) + '-' + ('0' + today.getDate()).slice(-2));
    const [time, setTime] = useState(('0' + today.getHours()).slice(-2) + ':' + ('0' + today.getMinutes()).slice(-2));
    const [coolDown, setCoolDown] = useState("25");
    const [data, setData] = useState([]);
    // const PipeName = ["ก็อกหน้าบ้าน (ถัง)", "ก็อกหน้าบ้าน (สปริงเกอร์)", "ก็อกสวนฝั่งซ้าย", "ก็อกสวนฝั่งขวา"];

        // getData.forEach((doc) => {
        //     
        // }); 

    useEffect(() => {
        async function fetchData() {
            try {
                const getData = await getDocs(collection(db, "Logs"));
                setData([]);
                getData.forEach((doc) => {
                    setData(oldData => [...oldData, doc.data()])
                  });
            }
            catch (err) {
                console.log(err.message);
            }
        };
        fetchData();
        console.log(data);
    }, []);
    return (
        <div>

            <NavBar />
            <div className="max-w-screen-sm mx-auto flex flex-col gap-2 p-4">
                {data.map( e =>
            <PipeStatusBox name={e.id} number={e.id} setIdFaucet={setIdFaucet} setToday={setToday} setDate={setDate} setTime={setTime}/>
            )}
                <PipeStatusBox name="ก็อกหน้าบ้าน (ถัง)" number="1" setIdFaucet={setIdFaucet} setToday={setToday} setDate={setDate} setTime={setTime}/>
                <PipeStatusBox name="ก็อกหน้าบ้าน (สปริงเกอร์)" number="2" setIdFaucet={setIdFaucet} setToday={setToday} setDate={setDate} setTime={setTime}/>
                <PipeStatusBox name="ก็อกสวนฝั่งซ้าย" number="3" setIdFaucet={setIdFaucet} setToday={setToday} setDate={setDate} setTime={setTime}/>
                <PipeStatusBox name="ก็อกสวนฝั่งขวา" number="4" setIdFaucet={setIdFaucet} setToday={setToday} setDate={setDate} setTime={setTime}/>
            </div>
            <PopUpSetTime id={idFaucet} date={date} setDate={setDate} time={time} setTime={setTime} coolDown={coolDown} setCoolDown={setCoolDown}/>
        </div>
        );
};
