function TogglePopUp(ID) {
    
    document.getElementById("popUp").classList.toggle("hidden");
    document.getElementById("popUp").classList.toggle("flex");
    document.getElementById("namePipePopUp").innerHTML = "ก็อก " + ID;
    // document.getElementById("namePipePopUp").innerHTML = ID;
    let saveID = document.getElementById("namePipePopUp").innerHTML.split(" ");

    document.getElementById("box_dot_" +saveID[1]).classList.toggle("bg-gray-600");
    document.getElementById("box_dot_" + saveID[1]).classList.toggle("bg-green-500");
    document.getElementById("dot_" + saveID[1]).classList.toggle("onBtnToggle");
}
export default TogglePopUp;
