// import { useState } from 'react';

export default function TextBox(props){
    function setValue(id) {
        if(id === "input_date"){
            document.getElementById("input_date").defaultValue = "";
        }
        else if(id === "input_time_turn_on"){
            var today = new Date();
            var time = today.getHours() + ':' + today.getMinutes();
            document.getElementById("input_time_turn_on").defaultValue = time;
        }
        
    }
    
    return (
    <div className="flex flex-col gap-2">
        <span className="text-2xl whitespace-nowrap">{props.label} : </span>
        <input id={props.idInput} className="rounded px-2 text-2xl" type={props.type} defaultValue={setValue(props.idInput)}></input>
    </div>
    );
};
