import { useState, useEffect } from "react";
import { db } from "../firebase"
import { getDocs, collection } from "firebase/firestore";

export default function PopUpLogs( { Turn, setTurn } ) {
    const [data, setData] = useState([]);

    const stylePopUp = "flex-col h-screen w-full bg-black/50 absolute top-0 left-0 z-10 items-center justify-center"

    useEffect(() => {
        async function fetchData() {
            setData([]);
            try {
                const getData = await getDocs(collection(db, "Logs"));
                getData.forEach((doc) => {
                    doc.data().history.forEach((history) => {
                        setData(oldData => [...oldData, {
                            id:doc.data().ID,
                            Date:history.Date,
                            TimeOpen:history.TimeOpen,
                            TimeClose:history.TimeClose,
                            Cooldown:history.Cooldown
                        }])
                    });
                });
            }
            catch (err) {
                console.log(err.message);
            }
        };
        fetchData();
    }, [Turn]);

    function handleClick(e) {
        e.preventDefault();
        setTurn(!Turn);
    }
    return (
        <div className={Turn ? "flex " + stylePopUp : "hidden " + stylePopUp}>
            <div className="bg-white rounded">
                <div className="flex justify-end bg-red-500 rounded-t p-2">
                    <h1 className="text-black" onClick={handleClick}>X</h1>
                </div>
                <div className="flex flex-col p-4">
                    {data.map((item, index) => (
                    <span className="text-black" key={String(index)}>{item.id} - {item.Date}</span>
                    ))}
                </div>
                
            </div>
            
            
                
        </div>
    )
}
