export function ProjectBox({ text,image,url }){
    
    const openNewTab = (link) => {
        console.log(link);
        window.open(link, '_blank', 'noreferrer');
      };
      
    return (
        <div className='flex flex-col justify-center items-center p-4'>
            <img className="md:w-3/5 mb-4" src={image} alt={text} />
            <span onClick={() => openNewTab(url)} className="text-white text-lg md:text-xl text-center cursor-pointer">{text}</span>
            
        </div>
        
      );
}
