// import { useState } from 'react';

export default function TextBox(props){
    // const [valueInput, setvalueInput] = useState('');
    function setTime(e) {
        var today = new Date();
        var time = today.getHours() + ':' + today.getMinutes();
        document.getElementById(e).value = time;
    }
    return (
    <div className="flex flex-col gap-2">
        <span className="text-2xl whitespace-nowrap">{props.label} : </span>
        <input id={props.idInput} className="rounded px-2 text-2xl" type={props.type} value={props.idInput === "" ? setTime(props.idInput) : ""}></input>
    </div>
    );
    
};
