import { useState } from "react";
import NavBar from "./navbar"
import PipeStatusBox from "../components/pipeStatusBox"
import PopUpSetTime from "../components/popUpSetTime"


export default function MainPage() {
    const [idFaucet,setIdFaucet] = useState("1");
    const [today,setToday] = useState(new Date());
    const [date, setDate] = useState(today.getFullYear() + '-' + ('0' + (today.getMonth() + 1)).slice(-2) + '-' + ('0' + today.getDate()).slice(-2));
    const [time, setTime] = useState(today.getHours() + ':' + today.getMinutes());
    const [coolDown, setCoolDown] = useState("25");
    return (
        <div>
            <NavBar />
            <div className="max-w-screen-sm mx-auto flex flex-col gap-2 p-4">
                <PipeStatusBox name="ก็อกหน้าบ้าน (ถัง)" number="1" setIdFaucet={setIdFaucet}/>
                <PipeStatusBox name="ก็อกหน้าบ้าน (สปริงเกอร์)" number="2" setIdFaucet={setIdFaucet}/>
                <PipeStatusBox name="ก็อกสวนฝั่งซ้าย" number="3" setIdFaucet={setIdFaucet}/>
                <PipeStatusBox name="ก็อกสวนฝั่งขวา" number="4" setIdFaucet={setIdFaucet}/>
            </div>
            <PopUpSetTime id={idFaucet} today={today} setToday={setToday} date={date} setDate={setDate} time={time} setTime={setTime} coolDown={coolDown} setCoolDown={setCoolDown}/>
        </div>
        );
};
