import { useState } from "react";
import NavBar from "./navbar"
import PipeStatusBox from "../components/pipeStatusBox"
import PopUpSetTime from "../components/popUpSetTime"


function mainPage() {
    const [id,setID] = useState("1");
    return (
        <div>
            <NavBar />
            <div className="max-w-screen-sm mx-auto flex flex-col gap-2 p-4">
                <PipeStatusBox name="ก็อก 1" number="1"/>
                <PipeStatusBox name="ก็อก 2" number="2"/>
                <PipeStatusBox name="ก็อก 3" number="3"/>
                <PipeStatusBox name="ก็อก 4" number="4"/>
            </div>
            <PopUpSetTime ID={id} />
        </div>
        
        
        );
}

export default mainPage;
