import {  useState,useEffect } from "react";
import TogglePopUp from "../functions/togglePopUp"

export default function PipeStatusBox( { name, number, turn, setIdFaucet, setToday, setDate, setTime}) {
  const [onOff,setOnOff] = useState(true);
  useEffect(() => {
    function setStyleBtn() {
      setOnOff(turn);
      if (onOff) {
        document.getElementById("box_dot_" + number).classList.toggle("bg-gray-600");
        document.getElementById("box_dot_" + number).classList.toggle("bg-green-500");
        document.getElementById("dot_" + number).classList.toggle("onBtnToggle");
      }
    };
    setStyleBtn()
}, []);

  function OpenPopUp(id) { 
    TogglePopUp(id,onOff); //ตั้ง style ของปุ่มเปิดปิด
    setIdFaucet(id); //ตั้ง ID ที่จะใช้อัพเดทข้อมูล
    // setOnOff(!onOff);
    const today = new Date();
    setToday(today);
    setDate(today.getFullYear() + '-' + ('0' + (today.getMonth() + 1)).slice(-2) + '-' + ('0' + today.getDate()).slice(-2));
    setTime(('0' + today.getHours()).slice(-2) + ':' + ('0' + today.getMinutes()).slice(-2));
  };
  return (
        <div className="bg-white rounded w-full p-2 flex justify-between">
            <h1 id="namePipe" className="text-2xl font-bold whitespace-nowrap">{name}</h1>
            <div className="relative cursor-pointer" onClick={() => OpenPopUp(number)}>
                <div id={"box_dot_" + number} className="box_dot block w-14 h-8 rounded-full bg-gray-600"></div>
                <div id={"dot_" + number}  className="dot absolute left-1 top-1 bg-white w-6 h-6 rounded-full transition"></div>
            </div>
        </div> 
  );
};
