import { collection, setDoc, doc, Timestamp } from "firebase/firestore";
// import { firestore } from "../firebase_setup/firebase"
import { db } from "../firebase"
import TextBox from "./textBoxInPopUp"
import TogglePopUp from "../functions/togglePopUp";

export default function PopUpSetTime({ id, date, setDate, time, setTime, coolDown, setCoolDown }) {

    const addLog = async (e) => {
        e.preventDefault();
        document.getElementById("popUp").classList.toggle("hidden");
        document.getElementById("popUp").classList.toggle("flex");
        try{
            // const res = await addDoc(collection(db, "Faucets"),{
            //     Date: date,
            //     Time: time,
            //     CoolDown: coolDown
            // })
            // await addDoc(doc(db, "Log", "001" ),{
            //     ID: id,
            //     Date: date,
            //     TimeOpen: time,
            //     TimeClose: "",
            //     CoolDown: coolDown + " นาที",
            //     TimeUpLoad : Timestamp.now()
            // })
            // db.collection("Pipe").doc("1").add({
            //     TimeUpLoad : Timestamp.now()
            // })
            await setDoc(doc(db, "Logs", "001" ),{
                    ID: id,
                    Date: date,
                    TimeOpen: time,
                    TimeClose: "",
                    CoolDown: coolDown + " นาที",
                    TimeUpLoad : Timestamp.now()
                })
        }catch(e){
            console.error(e);
        }
    }
    return (
          <div id="popUp" className="hidden h-screen w-full bg-black/50 absolute top-0 left-0 z-10 items-center justify-center">
                <div className="max-w-screen-sm mx-auto bg-slate-400 rounded flex flex-col">
                    <div className="bg-indigo-500 p-2 w-full rounded-t flex justify-between items-center">
                        <span id="namePipePopUp" className="text-2xl">ก๊อก {id}</span>
                        <span className="text-2xl cursor-pointer" onClick={() => TogglePopUp(id)}>X</span>
                    </div>
                    <div className="p-4 flex flex-col gap-4">
                        <TextBox idInput="input_date" label="วันที่" type="date" value={date} setValue={setDate} />
                        <TextBox idInput="input_time_turn_on" label="เริ่มเปิดน้ำ" type="time" value={time} setValue={setTime} />
                        <div className="flex flex-col gap-2">
                            <span className="text-2xl whitespace-nowrap">เปิดกี่นาที : </span>
                            <select className="text-2xl whitespace-nowrap" id="minuteID" form="minute" onChange={e => setCoolDown(e.target.value)}>
                                <option value="30">30 นาที</option>
                                <option value="25">25 นาที</option>
                                <option value="20">20 นาที</option>
                                <option value="15">15 นาที</option>
                                <option value="10">10 นาที</option>
                                <option value="5">5 นาที</option>
                            </select>
                        </div>
                        <button type="submit" className="w-full bg-indigo-500 rounded text-xl text-center p-2 cursor-pointer" onClick={addLog}>เพิ่ม</button>
                    </div>
                </div>
          </div> 
    );
  };
