function toggleBtn(ID) {
  document.getElementById("box_dot_" + ID).classList.toggle("bg-gray-600");
  document.getElementById("box_dot_" + ID).classList.toggle("bg-green-500");
  document.getElementById("dot_" + ID).classList.toggle("onBtnToggle");
}

function pipeStatusBox(props) {
  return (
        <div className="bg-white rounded w-full p-2 flex justify-between">
            <h1 className="text-3xl font-bold">{props.name}</h1>
            <div className="relative"onClick={() => toggleBtn(props.number)}>
                <div id={"box_dot_" + props.number} className="box_dot block w-14 h-8 rounded-full bg-gray-600"></div>
                <div id={"dot_" + props.number}  className="dot absolute left-1 top-1 bg-white w-6 h-6 rounded-full transition"></div>
            </div>
        </div> 
  );
}

export default pipeStatusBox;
