import { db } from "../firebase"
import { updateDoc, getDoc, doc } from "firebase/firestore";

export default async function TogglePopUp(ID) {
    const getDataByID = await getDoc(doc(db, "Logs", "00" + ID));
    if (getDataByID.data().TurnOnOff) {
        await updateDoc(doc(db, "Logs", "00" + ID), {
            TurnOnOff : false
        })
        document.getElementById("box_dot_" + ID).classList.toggle("bg-gray-600");
        document.getElementById("box_dot_" + ID).classList.toggle("bg-green-500");
        document.getElementById("dot_" + ID).classList.toggle("onBtnToggle");
    }
    else{
        document.getElementById("popUp").classList.toggle("hidden");
        document.getElementById("popUp").classList.toggle("flex");
        document.getElementById("box_dot_" + ID).classList.toggle("bg-gray-600");
        document.getElementById("box_dot_" + ID).classList.toggle("bg-green-500");
        document.getElementById("dot_" + ID).classList.toggle("onBtnToggle");
    }
};
