import { useState, useEffect } from "react";
import { db } from "../firebase"
import { getDocs, collection } from "firebase/firestore";

export default function PopUpLogs( { Turn, setTurn } ) {
    const [data, setData] = useState([]);
    const stylePopUp = "h-screen w-full bg-black/50 absolute top-0 left-0 z-10 items-center justify-center"

    useEffect(() => {
        async function fetchData() {
            try {
                const getData = await getDocs(collection(db, "Logs"));
                getData.forEach((doc) => {
                    doc.data().history.forEach((doc) => {
                        setData(oldData => [...oldData, doc])
                    });
                  });
            }
            catch (err) {
                console.log(err.message);
            }
            console.log(data);
        };
        fetchData();
    }, [Turn]);

    function handleClick(e) {
        e.preventDefault();
        setTurn(!Turn);
    }
    return (
        <div className={Turn ? "flex " + stylePopUp : "hidden " + stylePopUp}>
        <h1 onClick={handleClick}>text</h1>
        </div>
    )
}
